import { curry } from 'ramda';

const router = curry((api, req, res) => {
  switch (req.method) {
    case 'GET':
      api.get(req, res);
      break;
    case 'PUT':
      api.put(req, res);
      break;
    case 'POST':
      api.post(req, res);
      break;
    case 'DELETE':
      api.delete(req, res);
      break;
  }
});

export default router;